% fs: font size% ms: marker sizeglobal customfs;global customfn;global clearfigure;stdfontsize = 8;	% standard font sizestdmarkersize = 3;	% standard marker sizestdfigwidth = 3.5;	% standard figure width (to export in Illustrator)stdfigheight = 3.5;	% standard figure heightsmallfontsize = 6;smallmarkersize = 2;smallfigwidth = 1.5;smallfigheight = 1.5;fontsize = stdfontsize;markersize = stdmarkersize;figwidth = stdfigwidth;figheight = stdfigheight;%fs = 12;%ms = normalms;%ww = 8;%wh = 6;%gray = [.5 .5 .5];global cptf;	% counter for normal windowsglobal cptlf;	% counter for large windowshsp = 30;	% horizontally between windowsvsp = 45;	% vertically between windows%% normal windows% screensc = 1;	% 15 poucesscreensc = 1.2;	% 17 poucesscreensc = 1.45;	% 20 poucesnstdhwindows = 6;nstdvwindows = 4;stdwinwidth = screensc*200;stdwinheight = screensc*140;%% small windows% nstdhwindows = 8;% nstdvwindows = 5;% stdwinwidth = 150;% stdwinheight = 140;currentwinwidth = stdwinwidth;currentwinheight = stdwinheight;ncurrenthwindow = nstdhwindows;ncurrentvwindow = nstdvwindows;%% normal windows% stdwinwidth = 200;% wposx = [10 10  10  10  245 245 245 245 480 480 480 480 715 715 715 715 950 950 950 950 1070 1070 1070 1070];% stdwinheight = 140;% wposy = [25 210 395 580  25 210 395 580  25 210 395 580  25 210 395 580  25 210 395 580  25 210 395 580];%% small windowsllcpt = 1;for i=1:ncurrenthwindow	for j=1:ncurrentvwindow		wposx(llcpt) = 10 + (currentwinwidth+hsp)*(i-1);		llcpt = llcpt+1;	endendllcpt = 1;for i=1:ncurrenthwindow	for j=1:ncurrentvwindow		wposy(llcpt) = 45 + (currentwinheight+vsp)*(j-1);		llcpt = llcpt+1;	endend%% large windowlfw = 400;lfh = 400;CENTER = 6;SOUTH_WEST = 1;WEST = 2;NORTH_WEST = 3;NORTH = 7;NORTH_EAST = 11;EAST = 10;SOUTH_EAST = 9;SOUTH = 5;%TabDirections = [EAST NORTH_EAST NORTH NORTH_WEST WEST SOUTH_WEST SOUTH SOUTH_EAST];	% standardTabDirections = [SOUTH SOUTH_EAST EAST NORTH_EAST NORTH NORTH_WEST WEST SOUTH_WEST];TabPlotDirections = [SOUTH SOUTH_EAST EAST NORTH_EAST NORTH NORTH_WEST WEST SOUTH_WEST];	% rotated: 0 deg corresponds to movement toward the body% TabSubplotDirections = [8 9 6 3 2 1 4 7];TabSubplotDirections = [6 3 2 1 4 7 8 9];TabCenter = 5;%% COLORSi = 1;COLOR_WHITE = i; i = i+1;COLOR_BLACK = i; i = i+1;COLOR_LIGHT_GRAY = i; i = i+1;COLOR_GRAY = i; i = i+1;COLOR_DARK_GRAY = i; i = i+1;COLOR_LIGHT_RED = i; i = i+1;COLOR_RED = i; i = i+1;COLOR_DARK_RED = i; i = i+1;COLOR_LIGHT_GREEN = i; i = i+1;COLOR_GREEN = i; i = i+1;COLOR_DARK_GREEN = i; i = i+1;COLOR_LIGHT_BLUE = i; i = i+1;COLOR_BLUE = i; i = i+1;COLOR_DARK_BLUE = i; i = i+1;COLOR_LIGHT_YELLOW = i; i = i+1;COLOR_YELLOW = i; i = i+1;COLOR_DARK_YELLOW = i; i = i+1;ColorNames = [  'white      ';				'black      ';				'lightGray  ';				'gray       ';				'darkGray   ';				'lightRed   ';				'red        ';				'darkRed    ';				'lightGreen ';				'green      ';				'darkGreen  ';				'lightBlue  ';				'blue       ';				'darkBlue   ';				'lightYellow';				'yellow     ';				'darkYellow '];WHITE = [1 1 1];MYWHITE = [1 1 1];BLACK = [0 0 0];LIGHTGRAY = [.8 .8 .8];MYGRAY = [.6 .6 .6];DARKGRAY = [.4 .4 .4];LIGHTRED = [1 .4 .4];RED = [1 0 0];DARKRED = [.6 .1 .1];LIGHTGREEN = [0 1 0];GREEN = [0 .6 0];DARKGREEN = [.1 .3 .1];LIGHTBLUE = [.4 .4 1];BLUE = [0 0 1];NUBLUE = [.0742 0 .4844];DARKBLUE = [.1 .1 .5];LIGHTYELLOW = [1 1 .3];YELLOW = [1 1 0];DARKYELLOW = [.7 .7 .1];XLIGHTGREEN=[.9 1 .9];XLIGHTRED=[1 .9 .9];XLIGHTGRAY=[.9 .9 .9];XLIGHTBLUE=[.8 .95 .95];ORANGE = [1 .5 .3];BROWN=[.5 .2 .1];Colors = [WHITE;BLACK;LIGHTGRAY;MYGRAY;DARKGRAY;LIGHTRED;RED;DARKRED; ...	LIGHTGREEN;GREEN;DARKGREEN;LIGHTBLUE;BLUE;DARKBLUE; ...	LIGHTYELLOW;YELLOW;DARKYELLOW];%% STYLESSTYLE_SOLID = 1;STYLE_DOTTED = 2;STYLE_DASHDOT = 3;STYLE_DASHED = 4;StyleNames = [  'solid  ';				'dotted ';				'dashdot';				'dashed '];SOLID = '- ';DOTTED = ': ';DASHDOT = '-.';DASHED = '--';Styles = [SOLID;DOTTED;DASHDOT;DASHED];%% SYMBOLSSYMBOL_POINT = 1;SYMBOL_CIRCLE = 2;SYMBOL_XMARK = 3;SYMBOL_PLUS = 4;SYMBOL_STAR = 5;SYMBOL_SQUARE = 6;SYMBOL_DIAMOND = 7;SYMBOL_DOWNTRIANGLE = 8;SYMBOL_UPTRIANGLE = 9;SYMBOL_LEFTTRIANGLE = 10;SYMBOL_PENTAGRAM = 11;SYMBOL_HEXAGRAM = 12;SymbolNames = [ 'point    ';				'circle   ';				'xmark    ';				'plus     ';				'star     ';				'square   ';				'diamond  ';				'downTr   ';				'upTr     ';				'leftTr   ';				'rightTr  ';				'pentagram';				'hexagram '];POINT = '.';CIRCLE = 'o';XMARK = 'x';PLUS = '+';STAR = '*';MYSQUARE = 's';DIAMOND = 'd';DOWNTRIANGLE = 'v';UPTRIANGLE = '^';LEFTTRIANGLE = '<';RIGHTTRIANGLE = '>';PENTAGRAM = 'p';HEXAGRAM = 'h';Symbols = [POINT;CIRCLE;XMARK;PLUS;STAR;MYSQUARE;DIAMOND;DOWNTRIANGLE;UPTRIANGLE;LEFTTRIANGLE;RIGHTTRIANGLE;PENTAGRAM;HEXAGRAM];%% arrowsFIXED_ARROW = 1;PROPORTIONAL_ARROW = 2;defaultArrowLength = .1;defaultArrowProportion = .05;defaultArrowAngle = pi/24;