function u = pc(B, color, style, width)%PLOTCURVE plot a curve defined by vectors A and B.%   r = plotcurve( B, color, style, width) plots%   a curve with color, style, and line width%   #MATLAB 5/perso/graphics/plotcurve.m% u = plot(A, B, 'k');if nargin<4, width=1; endif nargin<3, style='-'; endif nargin<2, color=defcolor; endA = [1:length(B)];r = size(A);l = length(A);if (length(r) == 3)   % si A a 3dim, n'extrait qu'un vecteur de longueur length(A) , script inutile	l1 = r(1);	l2 = r(2);	l3 = r(3);	if (l1 == l)		for i=1:l %remplacer par A0=permute(A(:,1,1)) ?			A0(i) = A(i,1,1);		end	end		if (l2 == l)		for i=1:l			A0(i) = A(1,i,1);		end	end	if (l3 == l)		for i=1:l			A0(i) = A(1,1,i);		end	endelse	A0 = A;endr = size(B);l = length(B);if (length(r) == 3)	l1 = r(1);	l2 = r(2);	l3 = r(3);	if (l1 == l)		for i=1:l			B0(i) = B(i,1,1);		end	end		if (l2 == l)		for i=1:l			B0(i) = B(1,i,1);		end	end	if (l3 == l)		for i=1:l			B0(i) = B(1,1,i);		end	endelse	B0 = B;endu = plot(A0, B0);for uu=1:length(u)    if iscell(color), set(u(uu), 'Color', color{uu});    else set(u(uu), 'Color', color);    end    if length(width)>1, set(u(uu), 'LineWidth', width(uu));    else        set(u(uu), 'LineWidth', width);    end    if iscell(style), set(u(uu), 'LineStyle', style{uu});    else        set(u(uu), 'LineStyle', style);    endend